import React from 'react';
import { pink } from '@mui/material/colors';
import Checkbox from '@mui/material/Checkbox'
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

const label = { inputProps: { 'aria-label': 'Checkbox demo' } };
export default function Sidebar(props) {
    const {data,moneysort}=props;
    const [checkedState, setCheckedState] = React.useState(
      new Array(5).fill(false)
    );
    const checkbox = (event) => {
      document.getElementById("停車場").checked===true?alert(event.target.value):alert(1);
    };
    console.log(checkedState);
  return (
    <nav className="top-0 fixed lg:relative z-50 w-full flex flex-wrap items-center justify-between lg:justify-start navbar-expand-lg bg-white shadow-xl lg:overflow-y-auto flex-col lg:flex-nowrap lg:w-80 py-4 px-6 ">
       <h1>透過以下選項搜尋</h1>
       <hr size="100px" align="center" width="100%"></hr>
       <div>
       <div className="flex flex-col w-full sm:w-fit text-center sm:text-start mt-2 sm:mt-0 sm:ml-4 md:mx-6 sm:py-2">
        <Card sx={{ maxWidth: 500 }}>
        <CardContent>
        <input class="form-check-input" type="checkbox" value="" id="flexCheckDefault"/>
        <label class="form-check-label" for="flexCheckDefault">停車場</label>
        <input class="form-check-input" type="checkbox" value="" id="flexCheckDefault"/>
        <label class="form-check-label" for="flexCheckDefault">寵物友善</label>
        <input class="form-check-input" type="checkbox" value="" id="flexCheckDefault"/>
        <label class="form-check-label" for="flexCheckDefault">洗衣機</label>
        <input class="form-check-input" type="checkbox" value="" id="flexCheckDefault"/>
        <label class="form-check-label" for="flexCheckDefault">免費無線網路</label>
        <input class="form-check-input" type="checkbox" value="" id="flexCheckDefault"/>
        <label class="form-check-label" for="flexCheckDefault">禁煙客房</label>
        </CardContent>
        </Card>
        </div>
      </div>
    </nav>
  );
}
