import React from 'react';
import { pink } from '@mui/material/colors';
import Checkbox from '@mui/material/Checkbox'
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

const label = { inputProps: { 'aria-label': 'Checkbox demo' } };

function myFunction() {
  // Get the checkbox
  var checkBox = document.getElementById("myCheck");
  // Get the output text
  var text = document.getElementById("text");
  alert(0)
  // If the checkbox is checked, display the output text
  if (checkBox.checked == true){
    text.style.display = "block";
  } else {
    text.style.display = "none";
  }
}
export default function Sidebar(props) {
    const {data,moneysort}=props;
    const [checkedState, setCheckedState] = React.useState(
      new Array(5).fill(false)
    );
    const checkbox = (event) => {
      document.getElementById("Checkbox").checked===true?alert(event.target.value):alert(1);
    };
    console.log(checkedState);
  return (
    <nav className="top-0 fixed lg:relative z-50 w-full flex flex-wrap items-center justify-between lg:justify-start navbar-expand-lg bg-white shadow-xl lg:overflow-y-auto flex-col lg:flex-nowrap lg:w-80 py-4 px-6 ">
       <h1>透過以下選項搜尋</h1>
       <hr size="100px" align="center" width="100%"></hr>
       <div>
       <div className="flex flex-col w-full sm:w-fit text-center sm:text-start mt-2 sm:mt-0 sm:ml-4 md:mx-6 sm:py-2">
        <Card sx={{ maxWidth: 500 }}>
        <CardContent>
          <Typography variant="body2" labelId="demo-select-small" id="demo-select-small" >
          <Checkbox id="myCheck" onclick="checkbox()" />停車場<br></br>
          <Checkbox {...label} defaultChecked  value={1} />寵物友善<br></br>
          <Checkbox {...label} defaultChecked  value={2} />洗衣機<br></br>
          <Checkbox {...label} defaultChecked  value={3} />免費無線網路<br></br>
          <Checkbox {...label} defaultChecked  value={4} />禁煙客房<br></br>
          </Typography>
        </CardContent>
        </Card>
        </div>
      </div>
    </nav>
  );
}
