import React from 'react';

export default function House(props) {

  return (
    <>
    <div><img src={require(`../../hotelpic/${props.name}+".jpeg"`)}/></div>
      <div className="p-3 rounded-lg flex flex-row sm:flex-col  border hover:shadow-lg ease-linear transition-all duration-150">
        <div className="flex flex-col w-full sm:w-fit text-center sm:text-start mt-2 sm:mt-0 sm:ml-4 md:mx-6 sm:py-2">
          <div className="my-2 sm">
            <div className="text-xl md:text-2xl font-medium">{props.name}</div>
            <div className="text-md md:text-lg font-normal">{props.intro}</div>
            <div className="text-md md:text-lg font-normal">{props.score}</div>
            <div className="text-md md:text-lg font-normal">台北市</div>
            <div className="text-md md:text-lg font-normal">{props.price}</div>
          </div>
        </div>
      </div>
    </>
  );
}
